mapper.select:
SELECT id, cron_name cronName, cron, status, remark, class_name className, method_name methodName, is_run isRun, err_msg errMsg FROM cron_entity WHERE 1=1
AND is_run = #{isRun}
AND class_name = #{className}
AND method_name = #{className}

mapper.update:
UPDATE
  cron_entity
SET

      err_msg = #{errMsg},

  is_run = #{isRun}
WHERE
  class_name = #{className}
  AND method_name = #{methodName}
